mapper.select:
SELECT tbpostcmt.id ,tbpostcmt.deleted ,tbpostcmt.process ,tbpostcmt.created_at as createdAt ,tbpostcmt.modified_at as modifiedAt
,tbpostcmt.tbpost_id as tbpostId ,tbpostcmt.tbuser_id as tbuserId ,tbpostcmt.content
FROM tbpostcmt WHERE tbpostcmt.id = #{id}

SELECT tbpostcmt.id
FROM tbpostcmt
WHERE tbpostcmt.id is not NULL
AND tbpostcmt.deleted = #{deleted}
AND tbpostcmt.tbpost_id = #{tbpostId}
AND tbpostcmt.tbuser_id = #{tbuserId}
ORDER BY tbpostcmt.created_at DESC

SELECT tbpostcmt.id
FROM tbpostcmt
WHERE tbpostcmt.id is not NULL
AND tbpostcmt.deleted = #{deleted}
AND tbpostcmt.tbpost_id = #{tbpostId}
AND tbpostcmt.tbuser_id = #{tbuserId}



		AND STR_TO_DATE(#{cursor}, '%Y-%m-%d %H:%i:%s.%f') > tbpostcmt.created_at 
		ORDER BY tbpostcmt.created_at DESC


		AND tbpostcmt.created_at > STR_TO_DATE(#{cursor}, '%Y-%m-%d %H:%i:%s.%f')
		ORDER BY tbpostcmt.created_at ASC




		AND #{cursor} > tbpostcmt.tbuserId
		ORDER BY tbpostcmt.tbuserId DESC


		AND tbpostcmt.tbuserId > #{cursor}
		ORDER BY tbpostcmt.tbuserId ASC


LIMIT 0, #{perpage}

SELECT tbpostcmt.id
FROM tbpostcmt
WHERE tbpostcmt.id is not NULL
AND tbpostcmt.deleted = #{deleted}
AND tbpostcmt.tbpost_id = #{tbpostId}
AND tbpostcmt.tbuser_id = #{tbuserId}

ORDER BY tbpostcmt.created_at 
ORDER BY tbpostcmt.tbuserId 
ASC
DESC
LIMIT #{offset}, #{perpage}

SELECT count(tbpostcmt.id) as listsize
FROM tbpostcmt
WHERE tbpostcmt.id is not NULL
AND tbpostcmt.deleted = #{deleted}
AND tbpostcmt.tbpost_id = #{tbpostId}
AND tbpostcmt.tbuser_id = #{tbuserId}
