mapper.select:
SELECT tbpostfile.id ,tbpostfile.deleted ,tbpostfile.process ,tbpostfile.created_at as createdAt ,tbpostfile.modified_at as modifiedAt
,tbpostfile.tbpost_id as tbpostId ,tbpostfile.type ,tbpostfile.url
FROM tbpostfile WHERE tbpostfile.id = #{id}

SELECT tbpostfile.id
FROM tbpostfile
WHERE tbpostfile.id is not NULL
AND tbpostfile.deleted = #{deleted}
AND tbpostfile.tbpost_id = #{tbpostId}
AND tbpostfile.type = #{type}
ORDER BY tbpostfile.created_at DESC

SELECT tbpostfile.id
FROM tbpostfile
WHERE tbpostfile.id is not NULL
AND tbpostfile.deleted = #{deleted}
AND tbpostfile.tbpost_id = #{tbpostId}
AND tbpostfile.type = #{type}



		AND STR_TO_DATE(#{cursor}, '%Y-%m-%d %H:%i:%s.%f') > tbpostfile.created_at 
		ORDER BY tbpostfile.created_at DESC


		AND tbpostfile.created_at > STR_TO_DATE(#{cursor}, '%Y-%m-%d %H:%i:%s.%f')
		ORDER BY tbpostfile.created_at ASC




		AND #{cursor} > tbpostfile.type
		ORDER BY tbpostfile.type DESC


		AND tbpostfile.type > #{cursor}
		ORDER BY tbpostfile.type ASC


LIMIT 0, #{perpage}

SELECT tbpostfile.id
FROM tbpostfile
WHERE tbpostfile.id is not NULL
AND tbpostfile.deleted = #{deleted}
AND tbpostfile.tbpost_id = #{tbpostId}
AND tbpostfile.type = #{type}

ORDER BY tbpostfile.created_at 
ORDER BY tbpostfile.type 
ASC
DESC
LIMIT #{offset}, #{perpage}

SELECT count(tbpostfile.id) as listsize
FROM tbpostfile
WHERE tbpostfile.id is not NULL
AND tbpostfile.deleted = #{deleted}
AND tbpostfile.tbpost_id = #{tbpostId}
AND tbpostfile.type = #{type}
