mapper.select:
SELECT tbuser.id
FROM tbuser
WHERE tbuser.username = #{username}
  AND tbuser.password = #{password}

SELECT tbuser.id
FROM tbuser
WHERE tbuser.id is not NULL
AND tbuser.deleted = #{deleted}
AND tbuser.process = #{process}
AND tbuser.name LIKE CONCAT('%', #{name}, '%')
AND tbuser.nick = #{nick}
AND tbuser.phone LIKE CONCAT('%', #{phone})
ORDER BY tbuser.created_at DESC

SELECT tbuser.id
FROM tbuser
WHERE tbuser.id is not NULL
AND tbuser.deleted = #{deleted}
AND tbuser.process = #{process}
AND tbuser.name LIKE CONCAT('%', #{name}, '%')
AND tbuser.nick = #{nick}
AND tbuser.phone LIKE CONCAT('%', #{phone})

ORDER BY tbuser.created_at 
ORDER BY tbuser.name 
ASC
DESC
LIMIT #{offset}, #{perpage}

SELECT count(tbuser.id) as listsize
FROM tbuser
WHERE tbuser.id is not NULL
AND tbuser.deleted = #{deleted}
AND tbuser.process = #{process}
AND tbuser.name LIKE CONCAT('%', #{name}, '%')
AND tbuser.nick = #{nick}
AND tbuser.phone LIKE CONCAT('%', #{phone})
